mapper.select:
SELECT STU_ID AS ID, STU_PW AS PW, STU_NAME AS NAME, MAJOR, GRADE ,AUTHOR
FROM USER_STU
WHERE STU_ID = #{id}
AND STU_PW = #{pw}

SELECT PRO_ID AS ID, PRO_PW AS PW, PRO_NAME AS NAME, MAJOR ,AUTHOR
FROM USER_PRO
WHERE PRO_ID = #{id}
AND PRO_PW = #{pw}

SELECT EMP_ID AS ID, EMP_PW AS PW, EMP_NAME AS NAME,AUTHOR
FROM USER_EMP
WHERE EMP_ID = #{id}
AND EMP_PW = #{pw}
